import React from 'react';
import { TaskDetails, Request, Response } from '../entities/TaskManagmentEntities';
interface IProps {
    onAddTask: (task: TaskDetails) => void;
}

interface IState {
    taskName: string,
    taskImage: string
}

class AddTask extends React.Component<IProps, IState> {
    constructor(props: IProps) {
        super(props);

        this.state = {
            taskImage: "",
            taskName: "test"
        }

        this.handleTaskImageChange = this.handleTaskImageChange.bind(this);
        this.handleTaskNameChange = this.handleTaskNameChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleTaskNameChange(event: any) {
        this.setState({
            taskName: event.target.value,
            taskImage: this.state.taskImage
        });
    }

    handleTaskImageChange(event: any) {
        event.preventDefault();

        if (!event.target.files || event.target.files.length == 0 || event.target.files[0] == null) {
            alert("Image upload is required");
        }

        let file: Blob = event.target.files[0];
        let reader = new FileReader();

        reader.onloadend = () => {
            this.setState({
                taskName: this.state.taskName,
                taskImage: reader.result as string
            });
        }

        reader.readAsDataURL(file)
    }

    handleSubmit(event: { preventDefault: () => void; }) {
        if (!this.state.taskImage) {
            return alert("Upload image is required")
        }
        this.addTask();
        event.preventDefault();
    }

    clearForm() {
        this.setState({
            taskName: "",
            taskImage: ""
        });
    }

    addTask() {
        var request: Request.AddTask = {
            NewTask: {
                taskImage: this.state.taskImage,
                taskName: this.state.taskName
            }
        };

        const requestOptions: RequestInit = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(request)
        };

        fetch('api/TaskManagementApi/AddTask', requestOptions).then((response) => {
            if (response.ok) {
                return response.json();
            } else {
                alert('There was an error!');
            }
        })
            .then((response: Response.AddTask) => {
                this.props.onAddTask(request.NewTask);
                this.clearForm();
            })
            .catch((error) => {
                alert('There was an error!');
            });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <div className="card">
                    <div className="card-header h4">
                        Create new task
                    </div>
                    <div className="card-body fh-card-body">
                        <div className="form-group">
                            <label>Task name</label>
                            <input value={this.state.taskName} type="text" className="form-control" onChange={this.handleTaskNameChange} required />
                        </div>
                        <button type="button" className="btn btn-primary">
                            <label className="m-0 clickable" htmlFor="file">Upload Image </label>
                        </button>
                        <input id="file" style={{ visibility: 'hidden' }} type="file" accept="image/*" onChange={this.handleTaskImageChange} />
                        <img src={this.state.taskImage} className="pt-2" width="100%" />
                    </div>
                    <div className="card-footer">
                        <button type="submit" className="btn btn-primary">Add task</button>
                    </div>
                </div>
            </form>
        );
    }
}
export default AddTask
