import React from 'react';
import { TaskDetails, Request } from '../entities/TaskManagmentEntities';
interface IProps {
}

interface IState {
    newTask: TaskDetails
}

class AddTask extends React.Component<IProps, IState> {
    constructor(props: {} | Readonly<{}>) {
        super(props);

        this.state = {
            newTask: {
                TaskImage: "",
                TaskName: "test"
            }
        }

        this.handleTaskImageChange = this.handleTaskImageChange.bind(this);
        this.handleTaskNameChange = this.handleTaskNameChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleTaskNameChange(event: any) {
        debugger
        this.setState({
            newTask: {
                TaskName: event.target.value,
                TaskImage: this.state.newTask.TaskImage
            }
        });
    }

    handleTaskImageChange(event: any) {
        debugger
        event.preventDefault();

        let file = event.target.files[0];
        let reader = new FileReader();

        reader.onloadend = () => {
            this.setState({
                newTask: {
                    TaskName: this.state.newTask.TaskName,
                    TaskImage: reader.result
                }
            });
        }

        reader.readAsDataURL(file)
    }

    handleSubmit(event: { preventDefault: () => void; }) {
        this.addTask();
        event.preventDefault();
    }

    addTask() {
        var request: Request.AddTask = { NewTask: this.state.newTask }
        const requestOptions: RequestInit = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors',
            body: JSON.stringify(request)
        };
        fetch('api/TaskManagementApi/AddTask', requestOptions)
            .then(async response => {
                const data = await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.message) || response.status;
                    return Promise.reject(error);
                }

                //    this.setState({ postId: data.id })
            })
            .catch(error => {
                //this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <div className="card">
                    <div className="card-header h4">
                        Create new task
                </div>
                    <div className="card-body">
                        <div className="form-group">
                            <label>Task name</label>
                            <input value={this.state.newTask.TaskName} type="text" className="form-control" onChange={this.handleTaskNameChange} required />
                        </div>
                        <div className="form-group">
                            <input id="files" type="file" value={this.state.newTask.TaskImage} onChange={this.handleTaskImageChange} />
                        </div>
                        <img src={this.state.newTask.TaskImage} />
                    </div>
                    <div className="card-footer">
                        <button type="submit" className="btn btn-primary" onClick={this.addTask}>Add task</button>
                    </div>
                </div>
            </form>
        );
    }
}
export default AddTask
