import React from 'react';
class AddTask extends React.Component {
    constructor(props: {} | Readonly<{}>) {
        super(props);
        this.state = { value: '' };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event: { target: { value: any; }; }) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event: { preventDefault: () => void; }) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
    }

    addTask() {
        debugger
        const requestOptions: RequestInit = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors',
            body: JSON.stringify({ NewTask: { TaskName: 'noa', TaskImage: '555' } })
        };
        fetch('api/TaskManagementApi/AddTask', requestOptions)
            .then(async response => {
                const data = await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.message) || response.status;
                    return Promise.reject(error);
                }

                this.setState({ postId: data.id })
            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="card">
                <div className="card-header h4">
                    Create new task
                </div>
                <div className="card-body">
                    <div className="form-group">
                        <label>Task name</label>
                        <input type="text" className="form-control" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="files" className="btn">Upload image</label>
                        <input id="files" style={{ visibility: 'hidden' }} type="file" />
                    </div>
                </div>
                <div className="card-footer">
                    <button type="button" className="btn btn-primary" onClick={this.addTask}>Add task</button>
                </div>
            </div>
        );
    }
}
export default AddTask
