import React from 'react';
import { TaskDetails } from '../entities/TaskManagmentEntities';
interface IProps {
}

interface IState {
    error: string | null,
    isLoaded: boolean,
    tasks: TaskDetails[]
}
class TaskList extends React.Component<IProps, IState> {
    constructor(props: {} | Readonly<{}>) {
        super(props);
        this.state = {
            error: null,
            isLoaded: false,
            tasks: []
        };
    }

    loadTasks() {

        const requestOptions: RequestInit = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            mode: 'cors',
            body: JSON.stringify({})
        };

        fetch('api/TaskManagementApi/GetTasks', requestOptions)
            .then(async response => {debugger
                const data = await response.json();

                // check for error response
                if (!response.ok) {
                    // get error message from body or default to response status
                    const error = (data && data.message) || response.status;
                    return Promise.reject(error);
                }

                    this.setState({ tasks: data.tasks })
            })
            .catch(error => {
                //this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="card">
                <div className="card-body">
                    {this.state.tasks.map(task => <div>{task.TaskName}</div>)}
                </div>
                <div className="card-footer">
                    <button type="button" className="btn btn-primary" onClick={this.loadTasks.bind(this)}>Load tasks</button>
                </div>
            </div>
        );
    }
}
export default TaskList
